# Program to create an index of words and insert new words

words = []

# Function to create an index of words
def createIndex():
    for word in words:
        print("Word: {}, Index: {}".format(word, words.index(word)))

# Function to insert a new word
def insertWord(word):
    words.append(word)

# Main function
def main():
    createIndex()
    insertWord("hello")
    createIndex()

if __name__ == "__main__":
    main()
