import threading

def upper_string(string): 
    return string.upper()

def main(): 
    string = input("Enter a string: ")
    thread = threading.Thread(target = upper_string, args=(string,)) 
    thread.start() 
    thread.join() 
    print("Upper case string: ", upper_string(string)) 

if __name__ == "__main__": 
    main()
